import React, { Component } from 'react';
import './index.css';

const divStyle = {
    margin: '40px',
    border: '5px solid pink'
  };
  const pStyle = {
    fontSize: '15px',
    textAlign: 'center'
  };

class App extends Component {
  render() {
    return (
      <div className="App">
        <header className="2048">
          <h1 className="App-title">2048</h1>
        </header>
        <div>
            <table className='gameBoard'>
            {this.createGameboard()}
            </table>
        </div>
      </div>
    );
  }
  createGameboard = () => {
    let gameboard = []

    // Outer loop to create parent
    for (let i = 0; i < 4; i++) {
      let children = []
      //Inner loop to create children
      for (let j = 0; j < 4; j++) {
        children.push(<td className ='cell'>{`Column ${j + 1}`}</td>)
      }
      //Create the parent and add the children
      gameboard.push(<tr >{children}</tr>)
    }
    return gameboard
}
}


export default App;
