import React, { Component } from 'react';
import './index.css';

class App extends Component {
  render() {
    return (
      <div className="App">
        <header className="2048">
          <h1 className="App-title">2048</h1>
        </header>
        <div>
            <table style ={{'width': 400, 'height': 400, 'table-layout': 'fixed', 'border-width':10, 'border-color': 'black'}}>
            {this.createGameboard()}
            </table>
        </div>
      </div>
    );
  }
  createGameboard = () => {
    let gameboard = []

    // Outer loop to create parent
    for (let i = 0; i < 4; i++) {
      let children = []
      //Inner loop to create children
      for (let j = 0; j < 4; j++) {
        children.push(<td style= {{width: 100, height: 100, 'border-width':'5px', 'border-color': 'black'}}>{`Column ${j + 1}`}</td>)
      }
      //Create the parent and add the children
      gameboard.push(<tr style= {{width: 100, height: 100, 'border-width':10, 'border-color': 'black'}}>{children}</tr>)
    }
    return gameboard
}
}


export default App;
